let page = 1;

$(async function() {
		
		// ajax에서 동일하게 사용되는 속성 설정
		$.ajaxSetup({
			// ajax에서 한글 깨짐 문제 해결
			contentType : "application/x-www-form-urlencoded;charset=UTF-8",
			type : "post"
		});
		
		
		// BOARD 테이블의 전체 데이터를 가져오는 함수.
		 async function getList() {
			
			 await $.ajax({
				url : "reply_list.do",
				data : {
					no : bno,
					type : type,
					nickname : nickname,
					page : page
					},
				datatype : "xml", 
				success : async function(data) {
					
					$(".list table").remove();
					
					let xmlData = $.parseXML(data);
					let nickname1 = $(xmlData).find("nickname").text();
					let replies = $(xmlData).find("reply_list");
					let totalPage = $(xmlData).find("total_page").text(); // 총 페이지 수
					let page1 = $(xmlData).find("page").text(); 
					
					let table = "";
					
					table += "<tr>";
					
					replies.find("reply").each(async function() {
						
						table += "<table border='0' cellspacing='0' width='1000'>";
						table += "<td width='150'>"+$(this).find("comment_date").text()+"</td>"
						table += "<td width='100' class='comment_nickname'>"+$(this).find("comment_writer_nickname").text()+"</td>";
						table += "<td width='500' onmouseleave='return_box(this)' onmouseenter=\"change_box(\'"+$(this).find("comment_writer_nickname").text()+"\',this)\" class='cont' style='width: 400px'>"+$(this).find("comment_cont").text().replace(/\n/g, "<br>")+"</td>";
						
						if( $(this).find("comment_writer_nickname").text() == nickname1 ) {
							table += "<td width='75'> <input type='button' class='modify' value='수정하기' index='"+$(this).find("comment_index").text()+"'> </td>";
							table += "<td width='75'> <input type='button' class='delete' value='삭제' index='"+$(this).find("comment_index").text()+"'> </td>";
						}else {
							table += "<td width='75'> </td>";
							table += "<td width='75'> </td>";
						}
						
						table += "</tr>";
						table += "</table>";
						
					});
					
					
					$(".list").append(table);
					
					// 페이지 링크
					$(".page_link").empty();
					if (totalPage >= 1) {
						for (let i=1; i<=totalPage; i++) {
							if (i == page1) {
								$(".page_link").append("<b>" + i + "</b> ");
							} else {
								$(".page_link").append("<a href='#' class='page' page='" + i + "'>" + i + "</a> ");
							}
						}
					}
				},
				
				error : async function() {
					alert("데이터 통신 오류입니다!!!.~~~");
				}
			});
			
		}  // getList() 함수 end
		
		
		
		// 페이지 링크 클릭 이벤트 핸들러 등록
		$(".page_link").on("click", ".page", async function(e) {
		  e.preventDefault();
		  page = $(this).attr("page");
		  await getList();
		});
   
      
		        
		// 댓글 작성 버튼을 눌렀을 때 DB에 댓글이 저장.
		$("#replyBtn").on("click", function() {
			
			$.ajax({
				url : "reply_insert_ok.do",
				data : {	
						  writer_id: member_id,
						  writer_nickname: nickname,
					      cont: $("#re_content").val(),
					      bno: bno,
					      type: type
						},
				datatype : "html",
				success : function(data) {
					if(data > 0) {
						alert("댓글 작성 완료!!!");
						
						getList();
						
						$("#re_content").val("");  
						
					}else {
						alert("로그인이 필요합니다");
						location.href='member/login.jsp';
					}
				},
				
				error : function() {
					alert("데이터 통신 오류입니다.~~~");
				}
				});
		});
		
		$(".list").on("click", ".delete", function() {
			$.ajax({
				url : "reply_delete.do",
				data : {
					reply_index : $(this).attr("index"),
					member_id : member_id,
					type : type
				},
				datatype : "text",
				success : function(data) {
					if(data > 0) {
						alert("댓글 삭제 완료");
						getList();
					}else {
						alert("삭제 권한이 없습니다.");
					}
				},
				error : function() {
					alert("데이터 통신 오류입니다!!!.~~~");
				}
			});
		});	
		
		await getList();
		await thumbsCount();
		
		
	}); // onload end 
	
	function return_box(self){
		if($(self).find('textarea').length>0){
			let editedText = $(self).find('textarea').val();
	        $(self).html(editedText.trim().replace(/\n/g, "<br>"));
		}
	}
	function change_box(d, self) {
		if($("#hidden_nickname").val()==d){
			let text = $(self).html();
            let textarea = $('<textarea />', { 'class': 'cont', 'style': 'width: 450px; height: auto' });
            textarea.val(text.trim().replace(/<br>/g, '\n'));
            $(self).html(textarea);
            textarea.focus();
		}
	}
	
	
	async function thumbsCount() {
		await $.ajax({
			type : "post",
			url: "board_thumbs_count.do",
			data: {
				no: bno,
				type: type
			},
			success: async function(count) {
				$(".thumbs").html(count);
			},
			error: async function() {
				alert("데이터 통신 오류입니다!");
			}
		});
	}
	
	function thumbsClick() {
		$.ajax({
			type : "post",
			url : "board_thumbs.do",
			data : {
				no : bno,
				id : member_id,
				board_id : writer_id,
				type : type
			},
			datatype: "html",
			success : function(data) {
				
				if(data.trim() === 'cancel') {
					alert('좋아요를 취소하셨습니다.');
				}else {
					alert('좋아요를 누르셨습니다.');
				}
				
				
				thumbsCount();
			},
			error : function() {
				alert('데이터 통신 오류입니다.');
			}
		});
	}
	
	
	
