	$(".list").on("click", ".modify", function() {
		$.ajax({
			type : "post",
			url : "reply_modify.do",
			data : {
				reply_index : $(this).attr("index"),
				member_id : member_id,
				comment_cont : $(".cont").val(),
				type : type
			},
			datatype : "text",
			success : function(data) {
				if(data > 0) {
					alert("댓글 수정 완료");
					getList();
				}else {
					alert("본인이 작성한 댓글이 아닙니다");
				}
			},
			error : function() {
				alert("데이터 통신 오류입니다!!!.~~~");
			}
		});
	});
	
	
	
		 
